mapper.update:
update comment
content=#{content}, user_id=#{userId}, pid=#{pid}, root_id=#{rootId}, time=#{time}, fid=#{fid}, module=#{module},
where id=#{id}

mapper.delete:
delete
from comment
where fid = #{fid}
and module = #{module}

mapper.select:
select c1.*,u1.username as userName, u1.avatar as avatar, u2.username as replyName
from comment c1
left join user u1
on u1.id = c1.user_id
/* 递归查询*/
/*自查，通过c1.pid=c2.id查出自己的回复，再通过c2.user_id=u2.id查出回复的用户*/
left join comment c2
on c1.pid = c2.id
left join user u2
on u2.id = c2.user_id

    and c1.content like concat('%',#{content},'%')
    and c1.user_id = #{userId}
    and u1.username like concat('%',#{userName},'%')
    and c1.fid = #{fid}
    and c1.module = #{module}
    and c1.root_id = #{rootId}

order by c1.id desc

select comment.*,user.username as userName, user.avatar as avatar
from comment
left join user
on user.id = comment.user_id

    and content like concat('%',#{content},'%')
    and user_id = #{userId}
    and user.username like concat('%',#{userName},'%')
    and fid = #{fid}
    and module = #{module}
    /*pid等于空的都是根评论*/
    and pid is null
